"""
Ionic radii tables from CrysFML.

Adds ionic_radius[charge] for various ions.
"""

def init(table, reload=False):
    if 'ionic_radius' in table.properties and not reload: return
    table.properties.append('ionic_radius')

    # Function for interpreting ionization state and form factor tuple
    def chem_info_type(symbol, name, Z, mass, RCov, RWaals, volume, states,
                       radii, fermi_length, incoh, absorption):
        return Z, states, radii

    # Remove fortran continuation character, which is & at the end of the line
    # and remove data block / /.
    data = CFML_DATA.replace('&','').replace('/','')
    elements = eval(data)
    for Z, states, radii in elements:
        el = table[Z]
        el.ionic_radius = {}
        for charge,radius in zip(states,radii):
            if radius != 0.0:
                el.ionic_radius[charge] = radius


